// Utility to get workers by status
export const getWorkersByStatus = (
  status: 'working' | 'idle' | 'break' | 'responding'
): WorkerData[] => mockWorkers.filter((w) => w.status === status);

// Utility to get workers by role
export const getWorkersByRole = (role: WorkerData['role']): WorkerData[] =>
  mockWorkers.filter((w) => w.role === role);
